    public static ArrayList<wisata> getListData() {
        ArrayList<wisata> list = new ArrayList<>();
        for (String[] WisataData : data) {
            wisata wisata = new wisata();
            wisata.setId(Integer.parseInt(WisataData[0]));
            wisata.setName(WisataData[1]);
            wisata.setDescription(WisataData[2]);
            wisata.setPhoto(WisataData[3]);

            list.add(wisata);
        }
        return list;
    }
